package controlstmnt;

public class Multipicationprogram {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

			int a=6;
			System.out.println("Multiplication table of 6");
			for(int i=1;i<=10;i++)
			
				{
				System.out.println(i+"*"+a+"="+(a*i));
				}
				System.out.println();
			}

	}

