package controlstmnt;

public class elseif {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=20,b=10,c=30;
		if(a>b && a>c)
		{
			System.out.println(" a is greater");
		}
		else if(b>a && b>c)
		{
			System.out.println(" b is greater");
		}
		else if(b<c)
		{
			System.out.println(" c is greater");
		}

	}

}
